<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <link
      href="https://cdn.bootcdn.net/ajax/libs/twitter-bootstrap/3.4.1/css/bootstrap.css"
      rel="stylesheet"
    />
    <script src="https://cdn.bootcdn.net/ajax/libs/vue/2.6.14/vue.js"></script>

    <style>
      .container {
        margin: 60px auto;
        overflow: hidden;
      }
      button {
        outline: none !important;
      }
      .comment-list {
        margin: 10px 0;
      }
      td {
        padding: 10px !important;
      }
    </style>
  </head>

  <body>
    <div id="app">
      <div class="container">
        <div class="panel panel-info">
          <div class="panel-heading">
            <h3 class="panel-title">Vue </h3>
          </div>
          <div class="panel-body">
            <!--  start -->
            <commit-comment @func="getDataFromCommit"></commit-comment>
            <!--  end -->

            <!--  start -->
            <table class="table table-striped table-hover">
              <tbody>
                <tr v-for="(item,index) in list" :key="index">
                  <td>
                    <span class="pull-left">{{item.comment}}</span>
                    <span class="badge pull-right">{{item.username}}</span>
                  </td>
                </tr>
              </tbody>
            </table>
            <!--  end -->
          </div>
        </div>
      </div>
    </div>

    <!--  start -->
    <template id="commit-comment">
      <div class="comment-list">
        <div class="form-group">
          <!-- <label for="username"></label> -->
          <input
            id="username"
            type="text"
            class="form-control"
            v-model="username"
            placeholder=""
          />
        </div>
        <div class="form-group">
          <!-- <label for="comment"></label> -->
          <textarea
            id="comment"
            class="form-control"
            v-model="comment"
            placeholder=""
          ></textarea>
        </div>
        <div class="form-group">
          <button class="btn btn-info" @click="addComment"></button>
        </div>
      </div>
    </template>
    <!--  end -->

    <script>
      let vm = new Vue({
        el: "#app",
        data: {
          list: [],
        },
        methods: {
          getDataFromCommit() {
            this.list = JSON.parse(localStorage.getItem("commentList") || "[]");
          },
        },
        created() {
          // ,
          if (
            Array.isArray(
              JSON.parse(localStorage.getItem("commentList") || "[]")
            )
          ) {
            this.getDataFromCommit();
          } else {
            return;
          }
        },
        components: {
          "commit-comment": {
            template: "#commit-comment",
            data() {
              return {
                username: "",
                comment: "",
              };
            },
            methods: {
              addComment() {
                if (!this.username || !this.comment) {
                  alert(" ,  ~");
                } else {
                  //  commentList
                  let commentList = JSON.parse(
                    localStorage.getItem("commentList") || "[]"
                  );

                  // 
                  commentList.unshift({
                    username: this.username,
                    comment: this.comment,
                  });

                  // 
                  localStorage.setItem(
                    "commentList",
                    JSON.stringify(commentList)
                  );

                  this.username = this.comment = "";
                  this.$emit("func");
                }
              },
            },
          },
        },
      });
    </script>
  </body>
</html>
